        sec_cate = re.findall(r"<li>(.*?)</li>",cateInfo,re.S)
        for sec in sec_cate:
            sec_name = re.findall(r"<span>(.*?)</span>",sec,re.S)[0]
            thrid_url = re.findall(r'href="(.*?)"',sec,re.S)
            third_name = re.findall(r'title="(.*?)"',sec,re.S)
            a = [the_big_cate+'\t'+sec_name+'\t'+thrid_url[i]+'\t'+third_name[i] for i in range(len(thrid_url))]
            all_category_file.write(str(a)+"\n")
